import { useState } from 'react';
import { Form } from 'react-router-dom';
import { useFormik } from 'formik';
import * as yup from 'yup';

import classes from './AuthForm.module.css';

function AuthForm() {
  const [isLogin, setIsLogin] = useState(true);

  function switchAuthHandler() {
    setIsLogin((isCurrentlyLogin) => !isCurrentlyLogin);
  }

  const onSubmit = ({values, action}) => {
    console.log(values);
  }

  const formik = useFormik({
    initialValues : {
      email: '', password:'',
    },
    onSubmit,
    validationSchema : yup.object().shape({
      email : yup.string().email('Invalid email').required('Required'),
      password : yup.string().min(5,'Must Be 5 Characters Long').required('Required')
    })
  })

  return (
    <>
      <Form method="post" className={classes.form} onSubmit={onSubmit}>
        <h1>{isLogin ? 'Log in' : 'Create a new user'}</h1>
        <p>
          <label htmlFor="email">Email</label>
          <input id="email" type="email" name="email" onChange={formik.handleChange} onBlur={formik.handleBlur} />
          <span>{formik.errors}</span>
        </p>
        <p>
          <label htmlFor="image">Password</label>
          <input id="password" type="password" name="password" onChange={formik.handleChange} onBlur={formik.handleBlur} />
          <span>{formik.errors}</span>
        </p>
        <div className={classes.actions}>
          <button onClick={switchAuthHandler} type="button">
            {isLogin ? 'Create new user' : 'Login'}
          </button>
          <button>Save</button>
        </div>
      </Form>
    </>
  );
}

export default AuthForm;
